(92223, 8, 'Class 8', 2, 'Social Studies and Population Education', 1, 'We, Our Community and Nation', 0, 'Coming Soon....', '', '', '', ''),
(95749, 9, 'Class 9', 4, 'Social Studies', 4, 'Our Past', 0, 'Coming Soon....', '', '', '', ''),
